export default function GitignoreGenerator() {
    const [selectedTemplates, setSelectedTemplates] = useState<string[]>([]);
    const [customRules, setCustomRules] = useState<string>("");
    const [searchTerm, setSearchTerm] = useState("");
  
    const handleTemplateChange = (template: string) => {
      setSelectedTemplates((prev) =>
        prev.includes(template)
          ? prev.filter((t) => t !== template)
          : [...prev, template]
      );
    };
  
    const filteredTemplates = Object.keys(gitignoreTemplates).filter(template =>
      template.toLowerCase().includes(searchTerm.toLowerCase())
    );
  
    const generateGitignore = () => {
      const templates = selectedTemplates.map((t) => gitignoreTemplates[t]);
      return [...templates, customRules].join("\n");
    };
  
    const downloadGitignore = () => {
        const blob = new Blob([generateGitignore()], { type: "application/octet-stream" }); // Imposta un tipo generico di file
        const link = document.createElement("a");
        link.href = URL.createObjectURL(blob);
        link.download = ".gitignore"; 
        link.click();
      };
      
  
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white p-6">
        <div className="max-w-4xl mx-auto">
          <Link
            to="/"
            className="inline-flex items-center text-gray-400 hover:text-white mb-8 transition-colors"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Tools
          </Link>
  
          <PageHeader
            title=".gitignore Generator"
            description="Create and download customized .gitignore files"
            gradient="from-pink-400 to-blue-500"
          />
  
          <Card>
            <div className="space-y-6">
              <div>
              <div className="bg-gray-800 p-4 rounded text-gray-200 mb-4">
                <pre className="overflow-auto text-sm">
                  {generateGitignore() || "No rules selected or added."}
                </pre>
              </div>
  
              <button
                onClick={downloadGitignore}
                className="flex items-center px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 transition mb-8"
              >
                <Download className="mr-2 h-4 w-4" />
                Download .gitignore
              </button>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Select Templates
                </label>
                <input
                  type="text"
                  placeholder="Search Templates..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full p-3 mb-4 bg-gray-800 text-white rounded"
                />
                
                <div className="grid grid-cols-3 gap-4">
                  {filteredTemplates.map((template) => (
                    <label
                      key={template}
                      className="flex items-center space-x-2 text-gray-300"
                    >
                      <input
                        type="checkbox"
                        checked={selectedTemplates.includes(template)}
                        onChange={() => handleTemplateChange(template)}
                        className="text-purple-500 focus:ring-purple-500"
                      />
                      <span>{template}</span>
                    </label>
                  ))}
                </div>
              </div>
  
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Add Custom Rules
                </label>
                <textarea
                  value={customRules}
                  onChange={(e) => setCustomRules(e.target.value)}
                  className="w-full bg-gray-900 rounded p-3 text-white focus:ring-2 focus:ring-purple-500"
                  rows={5}
                  placeholder="Enter custom .gitignore rules"
                />
              </div>
  
              
            </div>
          </Card>
        </div>
      </div>
    );
  }
